local demos = {}

demos["hello_world.memo"] =
[[
--!:name
--Hello World!

function boot()
    clrs()
    print("Hello World!")
end

function tick()
    -- Draw a random tile at a random position with random colors
    tile(
        rnd(0, 15), rnd(0, 15), -- Random position
        char(rnd(0x00, 0x7f)),   -- Random char
        rnd(0, 15), rnd(0, 15)) -- Random colors
end

--!:font
--
]]

demos["splash.memo"] =
[[
--!:name
--Splash

frame = 0
print("frame: " .. frame)

function boot()
    clrs()
    print("Memo memo memo!")
    frame = 0
    print("frame: " .. frame)
end

function tick()
    clrs()
    frame = (frame + 1) % 0x100
    print(frame)

    for i = 0, 15 do
        val = i + frame
        tile(
            flr(val / 8), -- x pos
            val % 8, -- y pos
            val % 0x80 ,-- ASCII char
            val % 8, -- fg
            val + 3 % 8 -- bg
        )
    end
end

--!:font
--
]]


-- Export the demos
return demos