demos["splash.memo"] =
[[
--!:name
--Splash

frame = 0

function boot()
    clrs()
    fframe = 0
    bframe = 0
    text = "  \1 MEMOSAIC \1  " -- Must be 16+ chars
end

function tick()
    clrs()
    fframe = (fframe + 1)
    bframe = (bframe + 1)

    fcolor = flr((fframe / 16) % 16)
    bcolor = flr(((bframe / 16) + 10) % 16)
    if fcolor > 15 then return end

    for x = 0, 15 do
        for y = 0, 15 do
            if y >= frame then
                tile(x, y, text:sub(x + 1, x + 1), fcolor, bcolor)
            end
        end
    end
end

--!:font
--
]]


demos["snek.lua"] = [[
--!:name
--snek

LEFT=1
RIGHT=2
UP=3
DOWN=4
DIRS={
	{x=-1,y= 0},
	{x= 1,y= 0},
	{x= 0,y=-1},
	{x= 0,y= 1},
}

BLEFT=0
BRIGHT=1
BUP=2
BDOWN=3

t=0
body={}
apples={}
dir=UP

function v2(x,y)
	return {
		x=x,
		y=y,
	}
end

function
v2cpy(v)
	return {
		x=v.x,
		y=v.y,
	}
end

function
v2add(a,b)
	return {
		x=a.x+b.x,
		y=a.y+b.y,
	}
end

function
rndpos()
	return v2(
		rnd(16)-1,
		rnd(16)-1
	)
end

function
onbody(pos)
	for i,v
	in pairs(body)
	do
		if v.x==pos.x
		and v.y==pos.y
		then
			return true
		end
	end

	return false
end

function
onbodyexhead(
pos)
	for i=1,#body-1
	do
		local v
			=body[i]

		if v.x==pos.x
		and v.y==pos.y
		then
			return true
		end
	end

	return false
end

function
onapple(pos)
	for i,v in
	pairs(apples)
	do
		if v.x==pos.x
		and v.y==pos.y
		then
			return true
		end
	end

	return false
end

function
rndapple()
	local pos
		=rndpos()

	while
	onbody(pos)
	or onapple(pos)
	do
		pos=rndpos()
	end

	return pos
end

function
around(pos)
	local v
		=v2cpy(pos)

	if v.x<0
	then
		v.x=15
	end
	if v.x>=16
	then
		v.x=0
	end
	if v.y<0
	then
		v.y=15
	end
	if v.y>=16
	then
		v.y=0
	end

	return v
end

function
ate(head)
	local h=head

	for i,v in
	pairs(apples)
	do
		if h.x==v.x
		and h.y==v.y
		then
			return i
		end
	end

	return 0
end

function reset()
	local
		start=rndpos()

	t=0
	body={
		v2cpy(start),
		v2cpy(start),
		v2cpy(start),
		v2cpy(start),
	}
	dir=rnd(4)
	apples={

		--the number
		--of
		--`rndapple()`
		--is the
		--number
		--of apples
		--in the game

		rndapple(),
		rndapple(),
	}
end

function
updatedir()
	if btn(BUP)
	then
		if dir~=DOWN
		then
			dir=UP
		end

	elseif
	btn(BDOWN)
	then
		if dir~=UP
		then
			dir=DOWN
		end

	elseif
	btn(BLEFT)
	then
		if dir~=RIGHT
		then
			dir=LEFT
		end
	
	elseif
	btn(BRIGHT)
	then
		if dir~=LEFT
		then
			dir=RIGHT
		end
	end
end

function
updatebody()
	local pos
		=body[#body]
	local nxpos
		=v2add(
			pos,
			DIRS[dir]
		)
	
	nxpos
		=around(nxpos)
	
	insert(
		body,
		nxpos
	)

	rmv(body,1)
end

function
drawbody()
	for i,v
	in pairs(body)
	do
		tile(
			v.x,
			v.y,
			16+1,
			13,
			8
		)
	end
end

function
drawapples()
	for i,v in
	pairs(apples)
	do
		tile(
			v.x,
			v.y,
			16+2,
			7,
			13
		)
	end
end

function boot()
	reset()
end

function tick()
	t=t+1
	
	updatedir()

	if t%6 == 0
	then
		updatebody()

		local head
			=body[#body]
		local i
			=ate(head)

		if i~=0 then
			rmv(apples,i)
			insert(
				apples,
				rndapple()
			)
			insert(
				body,
				1,
				v2cpy(
					body[1]
				)
			)
		end

		if
		onbodyexhead(
			head
		)
		then
			reset()
		end

		clrs(16,0,0)
		drawapples()
		drawbody()
	end
end

--!:font
--

]]


-- Export the demos
return demos