demos["spincube.memo"] =
[[
--!:name
--spincube

--!:author
--TripleCubes

function
v3(x,y,z)
	return {
		x=x,
		y=y,
		z=z,
	}
end

t=0
verts={
	v3(-1, 1,-1),
	v3( 1, 1, 1),
	v3(-1, 1, 1),

	v3(-1, 1,-1),
	v3( 1, 1,-1),
	v3( 1, 1, 1),

	v3(-1,-1,-1),
	v3(-1,-1, 1),
	v3( 1,-1, 1),

	v3(-1,-1,-1),
	v3( 1,-1, 1),
	v3( 1,-1,-1),

	v3(-1, 1,-1),
	v3(-1, 1, 1),
	v3(-1,-1, 1),

	v3(-1, 1,-1),
	v3(-1,-1, 1),
	v3(-1,-1,-1),

	v3( 1, 1,-1),
	v3( 1,-1, 1),
	v3( 1, 1, 1),

	v3( 1, 1,-1),
	v3( 1,-1,-1),
	v3( 1,-1, 1),

	v3(-1, 1, 1),
	v3( 1, 1, 1),
	v3(-1,-1, 1),

	v3( 1, 1, 1),
	v3( 1,-1, 1),
	v3(-1,-1, 1),

	v3(-1, 1,-1),
	v3(-1,-1,-1),
	v3( 1, 1,-1),

	v3( 1, 1,-1),
	v3(-1,-1,-1),
	v3( 1,-1,-1),
}
depthbuf={}
chars={
	16+1,
	16+2,
	16+3,
	16+4,
	16+5,
	16+6,
	16+7,
	16+8,
	16+9,
	16+10,
}
local colors={
	3,4,5,6,7,8,
	10,11,12,13,
	14,15,
}

function
vrotx(v,r)
	return v3(
		v.x,

		v.y*cos(r)
		-v.z*sin(r),

		v.y*sin(r)
		+v.z*cos(r)
	)
end

function
vroty(v,r)
	return v3(
		v.x*cos(r)
		-v.z*sin(r),

		v.y,

		v.x*sin(r)
		+v.z*cos(r)
	)
end

function
vrotz(v,r)
	return v3(
		v.x*cos(r)
		-v.y*sin(r),

		v.x*sin(r)
		+v.y*cos(r),

		v.z
	)
end

function
vrot(v,x,y,z)
	local vx
		=vrotx(v,x)
	local vy
		=vroty(vx,y)
	local vz
		=vrotz(vy,z)
	return vz
end

function
vmap(v)
	return v3(
		v.x*8+8,
		v.y*8+8,
		v.z
	)
end

function
vmul(v,n)
	return v3(
		v.x*n,
		v.y*n,
		v.z*n
	)
end

function
vdiv(v,n)
	return v3(
		v.x/n,
		v.y/n,
		v.z/n
	)
end

function
vadd(a,b)
	return v3(
		a.x+b.x,
		a.y+b.y,
		a.z+b.z
	)
end

function
vsub(a,b)
	return v3(
		a.x-b.x,
		a.y-b.y,
		a.z-b.z
	)
end

function
vlen(v)
	return
		sqrt(v.x*v.x
			+v.y*v.y
			+v.z*v.z)
end

function
vnorm(v)
	return vdiv(
		v, vlen(v)
	)
end

function
vcross(a,b)
	return v3(
		a.y*b.z
		-a.z*b.y,

		a.z*b.x
		-a.x*b.z,

		a.x*b.y
		-a.y*b.x
	)
end

function
vdot(a,b)
	return
	a.x*b.x
	+a.y*b.y
	+a.z*b.z
end

function
vproj(v)
	return v3(
		v.x/-v.z,
		v.y/-v.z,
		v.z
	)
end

function
clamp(v,min,max)
	if v<min then
		return min
	end
	if v>max then
		return max
	end
	return v
end

function
ratio(
	x,
	x0,x1,
	y0,y1)
	local dx1
		=abs(x1-x0)
	local dx
		=abs(x-x0)
	local r=dx/dx1

	local dy1
		=y1-y0
	return
		dy1*r+y0
end

function
max3(a,b,c)
	if a>=b
	and a>=c then
		return a
	
	elseif b>=a
	and b>=c then
		return b

	else
		return c
	end
end

function
min3(a,b,c)
	if a<=b
	and a<=c then
		return a
	
	elseif b<=a
	and b<=c then
		return b

	else
		return c
	end
end

function
lnintersect(
	y,v1,v2
)
	if v1.x==v2.x
	then
		return v1.x
	end
	if v1.y==v2.y
	then
		return v1.x
	end

	local g
		=(v2.y-v1.y)
		/(v2.x-v1.x)
	local i
		=v1.y-(g*v1.x)
		
	local x=(y-i)/g
	local z=ratio(
		x,v1.x,v2.x,
		v1.z,v2.z
	)
	return x,z
end

function
intersect(
	y,v1,v2,v3
)
	y=y+0.5
	if v1.y>=y
	and v2.y>=y
	and v3.y>=y
	then
		return
			v1.x,v1.x,
			v1.z,v1.z
	end
	if v1.y<=y
	and v2.y<=y
	and v3.y<=y
	then
		return
			v1.x,v1.x,
			v1.z,v1.z
	end

	local sides={
		{v1,v2},
		{v2,v3},
		{v3,v1},
	}

	for i,v in
	pairs(sides) do
		if v[1].y>=y
		and v[2].y>=y
		then
			rmv(sides,i)
			break
		end

		if v[1].y<=y
		and v[2].y<=y
		then
			rmv(sides,i)
			break
		end
	end

	local x0,z0
		=lnintersect(
			y,
			sides[1][1],
			sides[1][2]
		)
	local x1,z1
		=lnintersect(
			y,
			sides[2][1],
			sides[2][2]
		)
	
	if x0>x1 then
		local swap=x0
		x0=x1
		x1=swap

		swap=z0
		z0=z1
		z1=swap
	end
	return x0,x1,
		z0,z1
end

function
ln(x0,x1,y,
z0,z1,char)
	if x0==x1 then
		return
	end

	for x
	=(x0),
	ceil(x1) do
		if x<0
		or x>=16 then
			goto cont
		end

		local z=ratio(
			x,x0,x1,
			z0,z1
		)

		if
		depthbuf
		[flr(y+1)]
		[flr(x+1)]
		>z then
			goto cont
		end

		depthbuf
		[flr(y+1)]
		[flr(x+1)]
			=z
		tile(
			flr(x),
			flr(y),
			chars[char],
			colors[
				flr(t/10)
				%#colors+1
			],
			0
		)

		::cont::
	end
end

function
scanln(v1,v2,v3,
char)
	local topy
		=min3(
			v1.y,
			v2.y,
			v3.y
		)
	topy=clamp(
		topy,0,15
	)
	local boty
		=max3(
			v1.y,
			v2.y,
			v3.y
		)
	boty=clamp(
		boty,0,15
	)

	for y
	=topy,
	boty
	do
		local x0,x1,
		z0,z1
			=intersect(
				y,v1,v2,v3
			)

		ln(
			x0,
			x1,
			y,
			z0,
			z1,
			char
		)
	end
end

function
v3drot(v)
	v=vrot(
		v,
		t/30,
		t/50,
		t/40
	)
	return v
end

function
v3d2d(v)
	v=vadd(
		v,
		v3(0,0,-3)
	)
	v=vproj(v)
	v=vmul(v,1.2)
	v=vmap(v)
	return v
end

function
clrdepthbuf()
	for y=1,16 do
	for x=1,16 do
		depthbuf[y][x]
			=-100
	end
	end
end

function boot()
	for y=1,16 do
		insert(
			depthbuf,
			{}
		)
		for x=1,16 do
			insert(
				depthbuf[y],
				0
			)
		end
	end
end

function tick()
	t=t+1
	clrs(0,0,0)
	clrdepthbuf()

	for i
	=1,#verts,3
	do
		local _v1
			=verts[i]
		local _v2
		 =verts[i+1]
		local _v3
		 =verts[i+2]

		_v1
			=v3drot(_v1)
		_v2
			=v3drot(_v2)
		_v3
			=v3drot(_v3)

		local nor
			=vcross(
				vsub(
					_v2,
					_v1
				),
				vsub(
					_v3,
					_v2
				)
			)
		nor=vnorm(nor)

		if vdot(
			nor,
			v3(0,0,-1)
		)<0 then
			goto cont
		end

		local char
			=vdot(
				vnorm(v3(
					0.6,
					0.7,
					-1
				)),
				nor
			)
		char=clamp(
			char,0,1
		)
		char
			=char*9.9+1
		char
			=flr(char)

		_v1=v3d2d(_v1)
		_v2=v3d2d(_v2)
		_v3=v3d2d(_v3)

		scanln(
			_v1,
			_v2,
			_v3,
			char
		)

		::cont::
	end
end



--!:font
--

]]

demos["snek.lua"] =
[[
--!:name
--snek

--!:author
--TripleCubes

LEFT=1
RIGHT=2
UP=3
DOWN=4
DIRS={
	{x=-1,y= 0},
	{x= 1,y= 0},
	{x= 0,y=-1},
	{x= 0,y= 1},
}

BLEFT=0
BRIGHT=1
BUP=2
BDOWN=3

t=0
body={}
apples={}
dir=UP

function v2(x,y)
	return {
		x=x,
		y=y,
	}
end

function
v2cpy(v)
	return {
		x=v.x,
		y=v.y,
	}
end

function
v2add(a,b)
	return {
		x=a.x+b.x,
		y=a.y+b.y,
	}
end

function
rndpos()
	return v2(
		rnd(16)-1,
		rnd(16)-1
	)
end

function
onbody(pos)
	for i,v
	in pairs(body)
	do
		if v.x==pos.x
		and v.y==pos.y
		then
			return true
		end
	end

	return false
end

function
onbodyexhead(
pos)
	for i=1,#body-1
	do
		local v
			=body[i]

		if v.x==pos.x
		and v.y==pos.y
		then
			return true
		end
	end

	return false
end

function
onapple(pos)
	for i,v in
	pairs(apples)
	do
		if v.x==pos.x
		and v.y==pos.y
		then
			return true
		end
	end

	return false
end

function
rndapple()
	local pos
		=rndpos()

	while
	onbody(pos)
	or onapple(pos)
	do
		pos=rndpos()
	end

	return pos
end

function
around(pos)
	local v
		=v2cpy(pos)

	if v.x<0
	then
		v.x=15
	end
	if v.x>=16
	then
		v.x=0
	end
	if v.y<0
	then
		v.y=15
	end
	if v.y>=16
	then
		v.y=0
	end

	return v
end

function
ate(head)
	local h=head

	for i,v in
	pairs(apples)
	do
		if h.x==v.x
		and h.y==v.y
		then
			return i
		end
	end

	return 0
end

function reset()
	local
		start=rndpos()

	t=0
	body={
		v2cpy(start),
		v2cpy(start),
		v2cpy(start),
		v2cpy(start),
	}
	dir=rnd(4)
	apples={

		--the number
		--of
		--`rndapple()`
		--is the
		--number
		--of apples
		--in the game

		rndapple(),
		rndapple(),
	}
end

function
updatedir()
	if btn(BUP)
	then
		if dir~=DOWN
		then
			dir=UP
		end

	elseif
	btn(BDOWN)
	then
		if dir~=UP
		then
			dir=DOWN
		end

	elseif
	btn(BLEFT)
	then
		if dir~=RIGHT
		then
			dir=LEFT
		end
	
	elseif
	btn(BRIGHT)
	then
		if dir~=LEFT
		then
			dir=RIGHT
		end
	end
end

function
updatebody()
	local pos
		=body[#body]
	local nxpos
		=v2add(
			pos,
			DIRS[dir]
		)
	
	nxpos
		=around(nxpos)
	
	insert(
		body,
		nxpos
	)

	rmv(body,1)
end

function
drawbody()
	for i,v
	in pairs(body)
	do
		tile(
			v.x,
			v.y,
			16+1,
			13,
			8
		)
	end
end

function
drawapples()
	for i,v in
	pairs(apples)
	do
		tile(
			v.x,
			v.y,
			16+2,
			7,
			13
		)
	end
end

function boot()
	reset()
end

function tick()
	t=t+1
	
	updatedir()

	if t%6 == 0
	then
		updatebody()

		local head
			=body[#body]
		local i
			=ate(head)

		if i~=0 then
			rmv(apples,i)
			insert(
				apples,
				rndapple()
			)
			insert(
				body,
				1,
				v2cpy(
					body[1]
				)
			)
		end

		if
		onbodyexhead(
			head
		)
		then
			reset()
		end

		clrs(16,0,0)
		drawapples()
		drawbody()
	end
end

--!:font
--

]]


